package com.NumberSystems;

public class AtmorphicNumber {


	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num=7;
		 int product=num*num;
         int rem=product%10; 
		 if(num==rem)
		 {
			 System.out.println(num+" is a atmorphic Number");
		 }
		 else
		 {
			 System.out.println(num+" is not a atmorphic number");
		 }
		 
		  
	}

}
